const links = new Set();
const hrefRegex = /href\s*=\s*["']([^"']+)["']/gi;
const excludePatterns = ['php','css','png','jpg','fonts','feed','comments','/categoria/','cdn','googletagmanager','zapwp','wp','whatsapp','facebook','twitter','youtube','tiktok','instagram'];

for (const item of $input.all()) {
  const html = item.json.data || '';
  let match;
  while ((match = hrefRegex.exec(html)) !== null) {
    let url = match[1].trim();
    if (!url) continue;

    // Excluir exactamente "/" (comparación exacta)
    if (url === '/') continue;

    // Excluir anclas y esquemas no HTTP útiles
    if (url.startsWith('#') || url.toLowerCase().startsWith('javascript:') ||
        url.toLowerCase().startsWith('mailto:') || url.toLowerCase().startsWith('tel:')) {
      continue;
    }

    // Excluir si contiene cualquiera de los patrones (case-insensitive)
    const lower = url.toLowerCase();
    if (excludePatterns.some(p => lower.includes(p))) continue;

    links.add(url);
  }
}

// Retornar links únicos como items para n8n
return Array.from(links).map(link => ({ json: { link } }));

